insert into roadkill.roadkill_pagecontent (Id, EditedBy, EditedOn, VersionNumber, Text, PageId) values
('b607079e-c1d9-41eb-9102-a35b160af6c1', 'admin', '2019-06-09 14:31', 1, '\r\n== PLEASE NOTE: == \r\n== - This action can NOT be reversed. == \r\n== - All transactions before the financial close off date can NOT be edited. == \r\n== - Transactions can NOT be created for a date prior to the financial close off date. == \r\n----\r\n== Step 1 == \r\n{{/l4IWJ_Zox561ShkyUS-NUQ5JI-V9mDR14g.png|}}\r\n === Click <em>Edit </em>from the home page.  === \r\n----\r\n== Step 2 == \r\n{{/bfQO8A9oVSEgbMb2AoXl_rHq4vHP5vsbFA.png|}}\r\n === Click on  === <em> === Close Date === </em>\r\n----\r\n== <strong >Step 3 == \r\n{{/VrnmCEaFJVi6vXPytqxdspvZHCruMCQWBQ.png|}}\r\nYou will be prompted to enter the System <em>Pin</em>. \r\n----\r\n== Step 4 == \r\n{{/QIu7hdthZky2-KEwH2luCfVwF8ylbjd8-w.png|}}\r\nSelect the <em>Close Date.</em>\r\nClick <em>Submit.</em>\r\n----\r\n== Step 5 == \r\n{{/fYuKnUFg9ei168EH36Vc1dRd4tra1pTd_g.png|}}\r\nThe<em> Close Date</em> will be shown in the home page once submitted. \r\nYou have successfully closed off the financial period. \r\n----\r\n----\r\n', @liid);
