insert into roadkill.roadkill_pagecontent (Id, EditedBy, EditedOn, VersionNumber, Text, PageId) values
('18eec4f3-9c21-4b6b-a1c6-488ebc77e5c4', 'admin', '2019-06-09 14:31', 1, '<p style="text-align: left;">\r\n<p style="text-align: left;"> ===  === \r\n== Step 1 == \r\n== : == \r\n<p style="text-align: left;">\r\n<p style="text-align: left;">{{/TjoFA13viFEM6kn4pY69jygxEObdkgxFVA.jpg|}}\r\n<p style="text-align: left;">\r\n<p style="text-align: left;"> === Enter the <em>Invoices</em> Menu under Accounts Receivable === \r\n<hr style="text-align: left;"><p style="text-align: left;"> === \r\n === \r\n<p style="text-align: left;">\r\n== Step 2: == \r\n<p style="text-align: left;">\r\n{{/ubnzYqCZ2KZFhXTT_1VqU6TAeQmpcAkZ2g.jpg|}}\r\n<p style="text-align: left;">\r\n<div style="text-align: left;"> === Click on <em>Add</em> to create a new Invoice === </div><hr style="text-align: left;"><p style="text-align: left;">\r\n<p style="text-align: left;">\r\n== Step 3: == \r\n<p style="text-align: left;">\r\n<p style="text-align: left;">{{/KTBGQEqOpisQJ9ns6EIDE5uq5rFuEmzZ2w.jpg|}}\r\n<p style="text-align: left;">\r\n<ol>\n<li>\r\n\n<em> === Invoice Number  === </em> === will be generated automatically. === \n</li>\n<li>\r\n === Select the <em>Unit</em> you wish to charge the levy to. === </li>\n<li>\r\n === The <em>Debtor</em> field will be completed automatically. === </li>\n<li>\r\n === Enter the <em>Invoice Date, Due Date</em> and <em>Apply Dated</em>. === </li>\n<li>\r\n === Add a <em>Description</em> if required. === </li>\n<li>\r\n === Click on the <em>+</em> icon at the bottom of the page and select the expense from the <em>chart</em> of accounts drop down menu. === </li>\n<li>\r\n === Enter the <em>Gross</em> amount ONLY. === </li>\n<li>\r\n === Select <em>GST</em> if the expense is GST inclusive. === </li>\n<li>\r\n === The <em>Net</em> and <em>Tax</em> columns will be calculated automatically when you click save. === </li>\n<li>\r\n === Click on the <em>save</em> icon at the bottom of the page to confirm the expense item. === </li>\n</ol><hr style="text-align: left;"><p style="text-align: left;">\r\n== Step 4: == \r\n<p style="text-align: left;">\r\n<p style="text-align: left;">{{/7YzV9QTdtfSmzpLRKYmwII33mI0AKuPEdQ.png|}}\r\n<p style="text-align: left;"> === \r\n === \r\n<p style="text-align: left;"> === Click <em>Save </em>to create the invoice in Uxtrata Core. === \r\n----\r\n<p style="text-align: left;"> ===  === \r\n', @liid);
